mapper.select:
select 

from order_details
where order_id = #{orderId,jdbcType=INTEGER}
  and item_id = #{itemId,jdbcType=INTEGER}

mapper.delete:
delete from order_details
where order_id = #{orderId,jdbcType=INTEGER}
  and item_id = #{itemId,jdbcType=INTEGER}

mapper.update:
update order_details


    num = #{num,jdbcType=INTEGER},


    price = #{price,jdbcType=INTEGER},


where order_id = #{orderId,jdbcType=INTEGER}
  and item_id = #{itemId,jdbcType=INTEGER}

update order_details
set num = #{num,jdbcType=INTEGER},
  price = #{price,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=INTEGER}
  and item_id = #{itemId,jdbcType=INTEGER}
